import React from 'react';
import './Carrito.css';

const Carrito = ({ carrito, onRemove, onConfirm, user }) => {
  // Calcula el total del carrito
  const calcularTotal = () => {
    return carrito
      .filter((item) => item.price !== undefined) // Asegurarse de que el precio existe
      .reduce((total, item) => total + (item.price || 0), 0) // Evita errores si el precio es undefined
      .toFixed(2);
  };

  // Maneja la confirmación de compra
  const handleConfirmPurchase = () => {
    if (!user) {
      alert('Debes iniciar sesión para confirmar la compra.');
      return;
    }
    onConfirm();
  };

  return (
    <div className="carrito-container">
      <h2>Carrito de Compras</h2>
      {carrito.length === 0 ? (
        <p>El carrito está vacío.</p>
      ) : (
        <div>
          <ul className="carrito-list">
            {carrito.map((producto, index) => (
              <li key={index} className="carrito-item">
                <img
                  src={producto.image || 'https://via.placeholder.com/150'}
                  alt={producto.name || 'Producto sin nombre'}
                  className="carrito-item-image"
                />
                <div className="item-info">
                  <p className="item-name">{producto.name || 'Producto sin nombre'}</p>
                  <p className="item-price">${producto.price ? producto.price.toFixed(2) : '0.00'}</p>
                </div>
                <button
                  className="remove-button"
                  onClick={() => onRemove(producto.id)}
                >
                  Eliminar
                </button>
              </li>
            ))}
          </ul>
          <div className="carrito-total">
            <h3>Total: ${calcularTotal()}</h3>
          </div>
          <button className="confirm-button" onClick={handleConfirmPurchase}>
            Confirmar Compra
          </button>
        </div>
      )}
    </div>
  );
};

export default Carrito;
