import React, { useState } from 'react';
import './AgregarProducto.css';

const AgregarProducto = ({ agregarProducto }) => {
  const [nuevoProducto, setNuevoProducto] = useState({
    nombre: '',
    precio: '',
    descuento: '',
    img: '',
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setNuevoProducto((prev) => ({
      ...prev,
      [name]: name === 'precio' || name === 'descuento' ? parseFloat(value) || 0 : value,
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!nuevoProducto.nombre || !nuevoProducto.precio || !nuevoProducto.img) {
      alert('Todos los campos son obligatorios.');
      return;
    }
    agregarProducto({ ...nuevoProducto, id: Date.now() });
    alert('Producto agregado correctamente.');
    setNuevoProducto({
      nombre: '',
      precio: '',
      descuento: '',
      img: '',
    });
  };

  return (
    <div className="agregar-producto-container">
      <h2>Agregar Nuevo Producto</h2>
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label htmlFor="nombre">Nombre del Producto</label>
          <input
            type="text"
            id="nombre"
            name="nombre"
            value={nuevoProducto.nombre}
            onChange={handleChange}
            required
          />
        </div>
        <div className="form-group">
          <label htmlFor="precio">Precio</label>
          <input
            type="number"
            id="precio"
            name="precio"
            value={nuevoProducto.precio}
            onChange={handleChange}
            required
          />
        </div>
        <div className="form-group">
          <label htmlFor="descuento">Descuento (%)</label>
          <input
            type="number"
            id="descuento"
            name="descuento"
            value={nuevoProducto.descuento}
            onChange={handleChange}
          />
        </div>
        <div className="form-group">
          <label htmlFor="img">URL de la Imagen</label>
          <input
            type="text"
            id="img"
            name="img"
            value={nuevoProducto.img}
            onChange={handleChange}
            required
          />
        </div>
        <button type="submit" className="btn-submit">
          Agregar Producto
        </button>
      </form>
    </div>
  );
};

export default AgregarProducto;
