import React, { useEffect, useState } from 'react';
import './Productos.css';

const Productos = () => {
  const [productos, setProductos] = useState([]);
  const [nuevoProducto, setNuevoProducto] = useState({ nombre: '', precio: '', descripcion: '' });

  useEffect(() => {
    fetch('http://localhost:8080/productos')
      .then((response) => response.json())
      .then((data) => setProductos(data))
      .catch((error) => console.error('Error al obtener productos:', error));
  }, []);

  const handleChange = (e) => {
    setNuevoProducto({
      ...nuevoProducto,
      [e.target.name]: e.target.value,
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    fetch('http://localhost:8080/productos', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(nuevoProducto),
    })
      .then(() => {
        alert('Producto añadido');
        setNuevoProducto({ nombre: '', precio: '', descripcion: '' });
      })
      .catch((error) => console.error('Error al añadir producto:', error));
  };

  return (
    <div>
      <h2>Gestión de Productos</h2>
      <form onSubmit={handleSubmit}>
        <input type="text" name="nombre" placeholder="Nombre" onChange={handleChange} />
        <input type="number" name="precio" placeholder="Precio" onChange={handleChange} />
        <input type="text" name="descripcion" placeholder="Descripción" onChange={handleChange} />
        <button type="submit">Añadir Producto</button>
      </form>
      <ul>
        {productos.map((producto) => (
          <li key={producto.id}>
            {producto.nombre} - ${producto.precio}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default Productos;
