import React from 'react';
import { useNavigate } from 'react-router-dom';
import './Productos.css';
import { calcularPrecioConDescuento } from '../utils';

const Productos = ({ searchTerm, onAddToCart, user }) => {
  const navigate = useNavigate();

  const productos = [
    { id: 1, nombre: 'Televisi칩n UHD 4K Sony', precio: 299.99, descuento: 15, img: './imagenes/television.jpg' },
    { id: 2, nombre: 'PS5 Pro', precio: 199.99, descuento: 0, img: './imagenes/ps5.jpg' },
    { id: 3, nombre: 'Iphone 16', precio: 399.99, descuento: 20, img: './imagenes/iphone16.jpg' },
    { id: 4, nombre: 'Nike Air Jordan', precio: 159.99, descuento: 0, img: './imagenes/nike.jpg' },
    { id: 5, nombre: 'Casaca Invierno Levis', precio: 499.99, descuento: 25, img: './imagenes/television.jpg' },
    { id: 6, nombre: 'Audifonos JBL', precio: 249.99, descuento: 0, img: './imagenes/television.jpg' },
    { id: 7, nombre: 'Pelota Mundial 2022', precio: 159.99, descuento: 10, img: './imagenes/television.jpg' },
    { id: 8, nombre: 'Perfume Hugo Boss', precio: 699.99, descuento: 0, img: './imagenes/television.jpg' },
    { id: 9, nombre: 'S치banas 2 plazas', precio: 89.99, descuento: 0, img: './imagenes/television.jpg' },
    { id: 10, nombre: 'Lampara de Lava', precio: 179.99, descuento: 30, img: './imagenes/television.jpg' },
  ];

  const productosFiltrados = productos.filter((producto) =>
    producto.nombre.toLowerCase().includes(searchTerm.toLowerCase())
  );

  return (
    <section className="productos">
      <div className="productos-header">
        <h2> Nuestros Productos</h2>
        {user?.rol === 'admin' && (
          <div className="header-action">
            <button
              className="add-product-button"
              onClick={() => navigate('/agregar-producto')}
            >
              Agregar Productos
            </button>
          </div>
        )}
      </div>
      <div className="productos-grid">
        {productosFiltrados.length > 0 ? (
          productosFiltrados.map((producto) => {
            const precioConDescuento = calcularPrecioConDescuento(producto.precio, producto.descuento);

            return (
              <div key={producto.id} className="producto-card">
                <img src={producto.img} alt={producto.nombre} />
                <h3>{producto.nombre}</h3>
                <div className={`precios ${producto.descuento === 0 ? 'no-descuento' : ''}`}>
                  {producto.descuento > 0 ? (
                    <>
                      <p className="precio-original">S/ {producto.precio.toFixed(2)}</p>
                      <p className="precio-con-descuento">
                        S/ {precioConDescuento.toFixed(2)} <span>({producto.descuento}% Dcto.)</span>
                      </p>
                    </>
                  ) : (
                    <p className="precio-sin-descuento">S/ {producto.precio.toFixed(2)}</p>
                  )}
                </div>
                <button className="btn" onClick={() => onAddToCart(producto)}>
                  Agregar al carrito
                </button>
              </div>
            );
          })
        ) : (
          <p>No se encontraron productos</p>
        )}
      </div>
    </section>
  );
};

export default Productos;
